import { useState } from 'react'

import MainGrid from '../src/components/MainGrid'
import Box from '../src/components/Box'
import {
	AlurakutMenu,
	AlurakutProfileSidebarMenuDefault,
	OrkutNostalgicIconSet,
} from '../src/lib/AlurakutCommons'
import { ProfileRelationsBoxWrapper } from '../src/components/ProfileRelations'

function ProfileSidebar(props) {
	return (
		<Box as="aside">
			<img
				src={`https://github.com/${props.githubUser}.png`}
				style={{ borderRadius: '8px' }}
			/>
			<hr />
			<p>
				<a className="boxLink" href={`https://github.com/${props.githubUser}`}>
					@{props.githubUser}
				</a>
			</p>

			<hr />

			<AlurakutProfileSidebarMenuDefault />
		</Box>
	)
}

export default function Home() {
	const githubUser = 'klayverxd'
	const [communities, setCommunities] = useState([
		{
			id: '111',
			title: 'Eu odeio acordar cedo',
			image: 'https://alurakut.vercel.app/capa-comunidade-01.jpg',
		},
		{
			id: '222',
			title: 'Eu nunca terminei uma borracha',
			image:
				'https://www.socialdub.com/groupspictures/7675/76751438640309192420.jpg?x2',
		},
		{
			id: '333',
			title: 'Queria sorvete, mas era feijão',
			image:
				'https://img10.orkut.br.com/community/5772468e52cea8b6dc2d07653185140b.jpg',
		},
		{
			id: '444',
			title: 'Eu abro a geladeira pra pensar',
			image:
				'data:image/jpeg;base64,',
		},
		{
			id: '555',
			title: 'Não fui eu, foi meu Eu lírico',
			image:
				'data:image/jpeg;base64,',
		},
		{
			id: '666',
			title: 'Eu nunca morri na minha vida',
			image:
				'data:image/jpeg;base64,',
		},
	])
	const pessoasFavoritas = [
		'alannapaiva',
		'williambrunos',
		'JanniellyGarcia',
		'RogVenancio',
		'LaisFSGomes',
		'jessicaMarquess',
	]

	function handleCreateComunity(e) {
		e.preventDefault()
		const formData = new FormData(e.target)

		const community = {
			id: new Date().toISOString(),
			title: formData.get('title'),
			image: formData.get('image'),
		}

		setCommunities([...communities, community])
	}

	return (
		<>
			<AlurakutMenu githubUser={githubUser} />
			<MainGrid>
				<div className="profileArea" style={{ gridArea: 'profileArea' }}>
					<ProfileSidebar githubUser={githubUser} />
				</div>
				<div className="welcomeArea" style={{ gridArea: 'welcomeArea' }}>
					<Box>
						<h1 className="title">Bem vindo(a)</h1>

						<OrkutNostalgicIconSet />
					</Box>
					<Box>
						<h2 className="titlesubTitle">O que você deseja fazer?</h2>

						<form onSubmit={handleCreateComunity}>
							<div>
								<input
									type="text"
									placeholder="Qual vai ser o nome da sua comunidade?"
									name="title"
									aria-label="Qual vai ser o nome da sua comunidade?"
								/>
							</div>
							<div>
								<input
									type="text"
									placeholder="Coloque o URL para usarmos de capa"
									name="image"
									aria-label="Coloque o URL para usarmos de capa"
								/>
							</div>
							<button>Criar comunidade</button>
						</form>
					</Box>
				</div>
				<div
					className="profileRelationsArea"
					style={{ gridArea: 'profileRelationsArea' }}
				>
					<ProfileRelationsBoxWrapper>
						<h2 className="smallTitle">Comunidades ({communities.length})</h2>

						<ul>
							{communities.map(community => {
								return (
									<li key={community.id}>
										<a href={`/users/${community}`}>
											<img src={community.image} />
											<span>{community.title}</span>
										</a>
									</li>
								)
							})}
						</ul>
					</ProfileRelationsBoxWrapper>
					<ProfileRelationsBoxWrapper>
						<h2 className="smallTitle">
							Pessoas da comunidade ({pessoasFavoritas.length})
						</h2>

						<ul>
							{pessoasFavoritas.map(user => {
								return (
									<li key={user}>
										<a href={`/users/${user}`}>
											<img src={`https://github.com/${user}.png`} />
											<span>{user}</span>
										</a>
									</li>
								)
							})}
						</ul>
					</ProfileRelationsBoxWrapper>
				</div>
			</MainGrid>
		</>
	)
}
